import React, { useState, useEffect } from "react";
import "./noticeboard.css"; // Make sure to import the CSS file for styling

const Noticeboard = () => {
  const [posts, setPosts] = useState([]);
  const [content, setContent] = useState("");
  const [userId, setUserId] = useState("user123"); // Assume you get this from login

  // Fetch posts from backend
  useEffect(() => {
    fetch("/posts")
      .then((res) => res.json())
      .then((data) => setPosts(data));
  }, []);

  // Handle post creation
  const handlePostSubmit = (e) => {
    e.preventDefault();
    fetch("/posts", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ content, userId }),
    })
      .then((res) => res.json())
      .then((newPost) => {
        setPosts([newPost, ...posts]);
        setContent("");
      });
  };

  // Handle liking a post
  const handleLike = (postId) => {
    fetch(`/posts/${postId}/like`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ userId }),
    }).then(() => {
      setPosts(posts.map((post) => (post._id === postId ? { ...post, liked: true } : post)));
    });
  };

  // Handle commenting on a post
  const handleComment = (postId, comment) => {
    fetch(`/posts/${postId}/comment`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ userId, comment }),
    }).then(() => {
      setPosts(posts.map((post) => (post._id === postId ? { ...post, comments: [...post.comments, { userId, comment }] } : post)));
    });
  };

  return (
    <div className="noticeboard">
      <h1 className="noticeboard-title">Noticeboard</h1>

      {/* Create post form */}
      <form className="create-post-form" onSubmit={handlePostSubmit}>
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="Write something..."
          className="create-post-textarea"
        />
        <button type="submit" className="create-post-btn">Post</button>
      </form>

      {/* Display posts in blocks */}
      <div className="posts-container">
        {posts.map((post) => (
          <div key={post._id} className="post-block">
            <div className="post-content">
              <p>{post.content}</p>
            </div>

            <div className="post-actions">
              <button className="like-btn" onClick={() => handleLike(post._id)}>
                Like
              </button>
              <span className="like-count">{post.likes.length} Likes</span>
            </div>

            {/* Comments section */}
            <div className="comments-section">
              <div className="comments-list">
                {post.comments.map((comment, idx) => (
                  <div key={idx} className="comment-item">
                    <strong>{comment.userId}:</strong> {comment.comment}
                  </div>
                ))}
              </div>
              <textarea
                placeholder="Add a comment"
                onBlur={(e) => handleComment(post._id, e.target.value)}
                className="comment-input"
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Noticeboard;
